jQuery(function($) {
    $("[data-animation]").each(function() {
        var $el = $(this), animationType = $el.data("animation"), animationDelay = $el.data("animationDelay") || 1, animationDirection = ~animationType.indexOf("Out") ? "back" : "forward";
        "forward" == animationDirection ? $el.one("inview", function() {
            setTimeout(function() {
                $el.addClass(animationType + " visible")
            }, animationDelay)
        }) : ($el.addClass("visible"), $el.one("click", function() {
            setTimeout(function() {
                $el.addClass(animationType)
            }, 0)
        }))
    })
});
